def make_char_map_4(graphics, b, e):
    width = 2048
    height = 8
    f = [0]*(width*height)
    for g in range(256):
        for d in range(8):
            for h in range(8):
                m = (graphics[((g + 256)<<3) + d] >> h) & 1
                n = (graphics[(g<<3) + d] >> h) & 1
                k = (h<<11) + (g<<3) + d
                f[k] = (m << 1) | n
    return np.array(f)

def get_sprites():
    img_4 = make_char_map_4(graphics, 0, 0).reshape(8, 2048)
    data = []
    for n in range(16):
        data.append(img_4[:, n*128:(n+1)*128])
    return np.array(data).reshape(128, 128)

def show_all_sprites(sprites):
    plt.figure(figsize=(8, 8))
    plt.imshow(sprites, cmap=cmaps[0])
    plt.show()

def get_sprite(sprites, sprite_index, offset=0):
    top = (sprite_index >> 4) << 3
    bottom = top + 8
    left = ((sprite_index & 0xf) << 3) + offset
    right = left + 8
    # print(top, left)
    return sprites[top:bottom, left:right]

def show_sprite(sprites, sprite_index, offset=0):
    plt.figure(figsize=(2, 2))
    plt.imshow(get_sprite(sprites, sprite_index, offset), cmap=cmaps[0])
    plt.show()

def write_sprite(f, sprite, sprite_num):
    for row in range(8):
        for col in range(8):
            word = int(sprite[row, col])
            suffix = ''
            if row == 0 and col == 0:
                suffix = f'// Sprite {sprite_num}'
            f.write(f'{word:02b} {suffix}\n')

if __name__ == '__main__':
    sprites = get_sprites()
    # show_all_sprites(sprites)
    bit_count = 0
    sprite_num = 0
    with open('sprites.txt', 'wt') as f:
        word_count = 0
        sprite_list = np.array([(255, 0), (44, 0), (45, 0), (48, 0), (49, 0), (20, 0), (21, 0), (28, 3),
            (0, 4), (10, 4), (14, 4), (16, 4), (120, 0), (121, 0), (122, 0), (123, 0), (96, 0),
            (97, 0), (98, 0), (99, 0), (100, 0), (101, 0), (102, 0), (103, 0), (104, 0),
            (105, 0), (107, 0), (108, 0), (109, 0), (58, 4), (60, 4), (62, 4)])

        for i, o in sprite_list:
            s = get_sprite(sprites, i, o)
            write_sprite(f, s, sprite_num)
            sprite_num += 1
            bit_count += 8*8*2
            word_count += 8*8
    print(f'{sprite_num} Sprites, {word_count} words, {bit_count>>3} bytes, {bit_count} bits')
